
public class Obj1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//속성(멤버변수)
		String color="빨간색";   //차량색상
		String model="BMW";	//모델명
		int power=200;;		//마력
		
		String color2="검정색";   //차량색상
		String model2="소나타";	//모델명
		int power2=170;;		//마력
		
		
		//객체의 속성값 출력
		System.out.printf("%s, %s, %d \n", model, color, power);
		System.out.printf("%s, %s, %d \n", model2, color2, power2);

	}

}
